import React, { useEffect, useState } from "react";

function Getuser() {
  const [users, setUsers] = useState([]);
  const [userValues, setUserValues] = useState([]);
  const [followers, setFollowers] = useState(0);

  const getUsers = async () => {
    const response = await fetch("https://api.github.com/users");
    setUsers(await response.json());
  };

  useEffect(() => {
    getUsers();
  }, [users]);

  const showDetails = (login) => {
    console.log("clicked");
    document.getElementsByClassName('show')[0].style.display="none";
    const response = fetch(`https://api.github.com/users/${login}`);
    setUserValues(response.json());
  };
  const getFollowers = async (login) => {
    fetch(`https://api.github.com/users/${login}`)
      .then((res) => res.json())
      .then((res) => {
        return res.followers;
      });
  };

  return (
    <>
      <h1 className="text-center">List of Github Users</h1>
      <div className="container container-fluid mt-5">
        <div className="clicked">
          {userValues.map((Elem) => {
            return (
              <>
                <h2>Details</h2>
                <div className="card p-2">
                  <div className="d-flex align-items-center">
                    <div className="image">
                      <img
                        src={Elem.avatar_url}
                        className="rounded"
                        width="155"
                      />
                    </div>
                    <div className="ml-3 w-100">
                      <h4 className="mb-0 mt-0 text-Left">{Elem.login}</h4>
                      <span className="text-left">{Elem.type}</span>
                      <div className="p-2 m-2 d-flex justify-content-between rounded bg-dark text-white">
                        
                      </div>
                    </div>
                  </div>
                </div>
              </>
            );
          })}
        </div>

        <div className="row text-center show">
          {users.map((curElem) => {
            return (
              <div className="col-10 col-md-4 mt-5">
                <div
                  className="card p-2"
                  onClick={() => showDetails(curElem.login)}
                >
                  <div className="d-flex align-items-center">
                    <div className="image">
                      <img
                        src={curElem.avatar_url}
                        className="rounded"
                        width="155"
                      />
                    </div>
                    <div className="ml-3 w-100">
                      <h4 className="mb-0 mt-0 text-Left">{curElem.login}</h4>
                      <span className="text-left">{curElem.type}</span>
                      <div className="p-2 m-2 d-flex justify-content-between rounded bg-dark text-white">
                        <div className="d-flex flex-column">
                          <span className="articles">Articles</span>{" "}
                          <span className="number1">38</span>{" "}
                        </div>
                        <div className="d-flex flex-column">
                          <span className="followers">followers</span>{" "}
                          <span className="number2">{}</span>{" "}
                        </div>
                        <div className="d-flex flex-column">
                          <span className="rating">Rating</span>{" "}
                          <span className="number3">8.9</span>{" "}
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
}

export default Getuser;
