function Show(){
    const [name, setName]= useState("");
    const [items, setItems] = useState([]);
    const [age, setAge]= useState(0);
    const [address, setAddress] = useState();
  
 
    const listOfItems=(e)=>{
      setItems((oldItems)=>{
      
        return [...oldItems, name, age,address];
        
      })
    };
  
  
  
  const apiData  =()=>{

    fetch(`https://jsonplaceholder.typicode.com/posts?userId=${age}`)
    .then((response) => response.json())
    .then((json) => console.log(json));
  }
    return (
      <div className="main">
        <div className="container">
          <br />
          <h2> Added Datas!!</h2>
          <br />
         
        <button onClick={apiData}>Show API Data</button>
        <ul>
          {items.map((itemval)=>{
            return (
                <li>{itemval}</li>
                )
          })}
        </ul>
        </div>
      </div>
    );
}

export default Show;
